'use strict';

const fileMd5 = require('../');
const assert = require('assert');

const expected = '38ae8007d11dbc777bba46ad3b3c5997'


describe('file md5 test',function(){
  it('sync type',function(){
    assert.equal(fileMd5('./README.md'),expected);
  })

  it('async type',function(done){
    fileMd5('./README.md',function(err,result){
      if(err){
        return done(err);
      }
      if(result == expected){
        done();
      }else{
        done(new Error('expected : ' + expected + ',actual :' + result))
      }
    })
  })
})